(223, 'searchtype', 'Search Type', 'Please select the type of search system you wish to use. You can either chose between \"Standard\", or \"Full Text\" (depending on your database). Fulltext searching is more powerful than the standard MyBB searching and quicker too.', 'php\n<select name=\\\"upsetting[{$setting[\'name\']}]\\\"><option value=\\\"standard\\\">\".($lang->setting_searchtype_standard?$lang->setting_searchtype_standard:\"Standard\").\"</option>\".($db->supports_fulltext(\"threads\") && $db->supports_fulltext_boolean(\"posts\")?\"<option value=\\\"fulltext\\\"\".($setting[\'value\']==\"fulltext\"?\" selected=\\\"selected\\\"\":\"\").\">\".($lang->setting_searchtype_fulltext?$lang->setting_searchtype_fulltext:\"Full Text\").\"</option>\":\"\").\"</select>', 'standard', 1, 21, 1),
(224, 'searchfloodtime', 'Search Flood Time (seconds)', 'Enter the time in seconds for the minimum allowed interval for searching. This prevents users from overloading your server by constantly performing searches. Set to 0 to disable.', 'numeric\nmin=0', '30', 2, 21, 1),
(225, 'minsearchword', 'Minimum Search Word Length', 'Enter the minimum number of characters an individual word in a search query can be. Set to 0 to disable (and accept the hard limit default of 3 for standard searching and 4 for MySQL fulltext searching). If you use MySQL fulltext searching and set this lower than the MySQL setting - MySQL will override it.', 'numeric\nmin=0', '0', 3, 21, 1),
(226, 'searchhardlimit', 'Hard Limit for Maximum Search Results', 'Enter the maximum amount of results to be processed. Set to 0 to disable. On larger boards (more than 1 million posts) this should be set to No more than 1000.', 'numeric\nmin=0', '0', 4, 21, 1),
