(1148, 'search', '<html>\r\n<head>\r\n<title>{$mybb->settings[\'bbname\']} - {$lang->search}</title>\r\n{$headerinclude}\r\n</head>\r\n<body>\r\n{$header}\r\n<form action=\"search.php\">\r\n<input type=\"hidden\" name=\"action\" value=\"do_search\" />\r\n<table border=\"0\" cellspacing=\"{$theme[\'borderwidth\']}\" cellpadding=\"{$theme[\'tablespace\']}\" class=\"tborder\">\r\n<tr>\r\n<td colspan=\"2\" class=\"thead\"><strong>{$lang->search}</strong></td>\r\n</tr>\r\n<tr>\r\n<td class=\"tcat\" width=\"50%\"><strong style=\"color: #555;\">{$lang->search_keyword}</strong></td>\r\n<td class=\"tcat\" width=\"50%\"><strong style=\"color: #555;\">{$lang->search_username}</strong></td>\r\n</tr>\r\n<tr>\r\n<td class=\"trow1\">\r\n<table><tr>\r\n<td valign=\"top\"><input type=\"text\" class=\"textbox\" name=\"keywords\" size=\"35\" maxlength=\"250\" /></td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<span class=\"smalltext\"><input type=\"radio\" class=\"radio\" name=\"postthread\" value=\"1\" checked=\"checked\" />{$lang->search_entire_post}<br /><input type=\"radio\" class=\"radio\" name=\"postthread\" value=\"2\" />{$lang->search_titles_only}</span></td>\r\n</tr></table>\r\n</td>\r\n<td class=\"trow1\">\r\n<input type=\"text\" class=\"textbox\" id=\"author\" name=\"author\" size=\"35\" maxlength=\"{$mybb->settings[\'maxnamelength\']}\" /><br /><span class=\"smalltext\"><input type=\"checkbox\" class=\"checkbox\" name=\"matchusername\" value=\"1\" checked=\"checked\" /> {$lang->match_username}</span>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td class=\"tcat\"><strong style=\"color: #555;\">{$lang->search_forums}</strong></td>\r\n<td class=\"tcat\"><strong style=\"color: #555;\">{$lang->search_options}</strong></td>\r\n</tr>\r\n<tr>\r\n<td class=\"trow1 no_bottom_border\" rowspan=\"{$rowspan}\">{$srchlist}</td>\r\n<td class=\"trow1\">\r\n<select name=\"findthreadst\">\r\n<option value=\"1\">{$lang->threads_at_least}</option>\r\n<option value=\"2\">{$lang->threads_at_most}</option>\r\n</select> <input