(1318, 'showthread_ratethread', '<div class=\"star_rating_container\">\r\n        <script type=\"text/javascript\" src=\"jscripts/rating.js?ver=1400\"></script>\r\n        <div id=\"success_rating_{$thread[\'tid\']}\" style=\"float: left; padding-top: 2px; padding-right: 10px;\">&nbsp;</div>\r\n        <div class=\"inline_rating\">\r\n\r\n<ul class=\"star_rating{$not_rated}\" id=\"rating_thread_{$thread[\'tid\']}\">\r\n<li style=\"width: {$thread[\'width\']}%\" class=\"current_rating\" id=\"current_rating_{$thread[\'tid\']}\">{$ratingvotesav}</li>\r\n<li><a class=\"five_stars\" title=\"{$lang->five_stars}\" href=\"./ratethread.php?tid={$thread[\'tid\']}&amp;rating=5&amp;my_post_key={$mybb->post_code}\"></a></li>\r\n<li><a class=\"four_stars\" title=\"{$lang->four_stars}\" href=\"./ratethread.php?tid={$thread[\'tid\']}&amp;rating=4&amp;my_post_key={$mybb->post_code}\"></a></li>\r\n<li><a class=\"three_stars\" title=\"{$lang->three_stars}\" href=\"./ratethread.php?tid={$thread[\'tid\']}&amp;rating=3&amp;my_post_key={$mybb->post_code}\"></a></li>\r\n<li><a class=\"two_stars\" title=\"{$lang->two_stars}\" href=\"./ratethread.php?tid={$thread[\'tid\']}&amp;rating=2&amp;my_post_key={$mybb->post_code}\"></a></li>\r\n<li><a class=\"one_star\" title=\"{$lang->one_star}\" href=\"./ratethread.php?tid={$thread[\'tid\']}&amp;rating=1&amp;my_post_key={$mybb->post_code}\"></a></li>\r\n            </ul>\r\n        </div>\r\n</div>', 3, '1812', '', 1616010337),
(1319, 'usercp', '<html>\r\n<head>\r\n<title>{$lang->user_cp}</title>\r\n{$headerinclude}\r\n</head>\r\n<body>\r\n{$header}\r\n<div id=\"nav_width\" style=\"width:24%;float:left;\">{$usercpnav}</div>\r\n<div id=\"content_width\" style=\"width:74%;float:right;\">\r\n<td valign=\"top\">\r\n<table border=\"0\" cellspacing=\"{$theme[\'borderwidth\']}\" cellpadding=\"{$theme[\'tablespace\']}\" class=\"tborder\">\r\n<tr>\r\n<td class=\"thead\" colspan=\"{$colspan}\"><strong>{$lang->account_summary}</strong></td>\r\n</tr>\r\n<tr>\r\n{$avatar}\r\n<td class=\"trow2\">\r\n<span class=\"largetext\">{$username}</span><br />\r\n<strong>{$lang->postnum}</strong> <a href=\"search.php?action=finduser&amp;uid={$mybb->user[\'uid\']}\">{$mybb->user[\'posts\']}</a> {$lang->posts_day}<br />\r\n{$reputation}\r\n<strong>{$lang->email}</strong> {$mybb->user[\'email\']}<br />\r\n<strong>{$lang->registration_date}</strong> {$regdate}<br />\r\n<strong>{$lang->primary_usergroup}</strong> {$usergroup}<br />\r\n{$referral_info}\r\n</td>\r\n</tr>\r\n</table>\r\n{$latest_subscribed}\r\n{$latest_threads}\r\n{$latest_warnings}\r\n{$user_notepad}\r\n</td>\r\n	</div>\r\n<br>\r\n{$footer}\r\n</body>\r\n</html>', 3, '1812', '', 1616010337),
(1320, 'usercp_addsubscription_thread', '<html>\r\n<head>\r\n<title>{$lang->subscribe_to_thread}</title>\r\n{$headerinclude}\r\n</head>\r\n<body>\r\n{$header}\r\n<form action=\"usercp2.php\" method=\"post\" name=\"input\">\r\n<input type=\"hidden\" name=\"my_post_key\" value=\"{$mybb->post_code}\" />\r\n<input type=\"hidden\" name=\"action\" value=\"do_addsubscription\" />\r\n<input type=\"hidden\" name=\"tid\" value=\"{$thread[\'tid\']}\" />\r\n<div id=\"nav_width\" style=\"width:24%;float:left;\">{$usercpnav}</div>\r\n<div id=\"content_width\" style=\"width:74%;float:right;\">\r\n<td valign=\"top\">\r\n<table border=\"0\" cellspacing=\"{$theme[\'borderwidth\']}\" cellpadding=\"{$theme[\'tablespace\']}\" class=\"tborder\">\r\n<tr>\r\n<td class=\"thead\" colspan=\"2\"><strong>{$lang->subscribe_to_thread}</strong></td>\r\n</tr>\r\n<tr>\r\n<td class=\"trow1\" valign=\"top\" width=\"200\"><strong>{$lang->notification_method}</strong></td>\r\n<td class=\"trow1\" valign=\"top\">\r\n	<table width=\"100%\" border=\"0\" align=\"center\">\r\n		<tr>\r\n			<td width=\"1\"><input type=\"radio\" name=\"notification\" id=\"notification_none\" value=\"0\" {$notification_none_checked} /></td>\r\n			<td><strong><label for=\"notification_none\">{$lang->no_notification}</label></strong><br /><span class=\"smalltext\">{$lang->no_notification_desc}</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td width=\"1\"><input type=\"radio\" name=\"notification\" id=\"notification_email\" value=\"1\" {$notification_email_checked} /></td>\r\n			<td><strong><label for=\"notification_email\">{$lang->email_notification}</label></strong><br /><span class=\"smalltext\">{$lang->email_notification_desc}</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td width=\"1\"><input type=\"radio\" name=\"notification\" id=\"notification_pm\" value=\"2\" {$notification_pm_checked} /></td>\r\n			<td><strong><label for=\"notification_pm\">{$lang->pm_notification}</label></strong><br /><span class=\"smalltext\">{$lang->pm_notification