(1322, 'usercp_avatar', '<html>\r\n<head>\r\n<title>{$mybb->settings[\'bbname\']} - {$lang->change_avatar}</title>\r\n{$headerinclude}\r\n</head>\r\n<body>\r\n{$header}\r\n<form enctype=\"multipart/form-data\" action=\"usercp.php\" method=\"post\">\r\n<input type=\"hidden\" name=\"my_post_key\" value=\"{$mybb->post_code}\" />\r\n<div id=\"nav_width\" style=\"width:24%;float:left;\">{$usercpnav}</div>\r\n<div id=\"content_width\" style=\"width:74%;float:right;\">\r\n	<td valign=\"top\">\r\n		{$avatar_error}\r\n		<table border=\"0\" cellspacing=\"{$theme[\'borderwidth\']}\" cellpadding=\"{$theme[\'tablespace\']}\" class=\"tborder\">\r\n			<tr>\r\n				<td class=\"thead\" colspan=\"2\"><strong>{$lang->change_avatar}</strong></td>\r\n			</tr>\r\n			<tr>\r\n				<td class=\"trow1\" colspan=\"2\">\r\n					<table cellspacing=\"0\" cellpadding=\"0\" width=\"100%\">\r\n						<tr>\r\n							<td class=\"hide_mobile\">{$lang->avatar_note}{$avatarmsg}</td>\r\n							{$currentavatar}\r\n						</tr>\r\n					</table>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td class=\"tcat\" colspan=\"2\"><strong>{$lang->custom_avatar}</strong></td>\r\n			</tr>\r\n			{$avatarupload}\r\n			{$avatar_remote}\r\n		</table>\r\n\r\n		<br />\r\n		<div align=\"center\">\r\n			<input type=\"hidden\" name=\"action\" value=\"do_avatar\" />\r\n			<input type=\"submit\" class=\"button\" name=\"submit\" value=\"{$lang->change_avatar}\" />\r\n			{$removeavatar}\r\n		</div>\r\n	</td>\r\n	</div>\r\n</form>\r\n{$footer}\r\n</body>\r\n</html>', 3, '1812', '', 1616010337),
